"use strict";

(function init100vh() {
  function setHeight() {
    var vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', "".concat(vh, "px"));
  }
  setHeight();
  window.addEventListener('resize', setHeight);
})();
window.addEventListener("orientationchange", function () {
  location.reload(true);
});

// padding no-ios
document.addEventListener("DOMContentLoaded", function () {
  var bannerDetDate = document.querySelector('.banner__details-date');
  var bannerDetTime = document.querySelector('.banner__details-time');
  if (!/iPad|iPhone|iPod|Mac/.test(navigator.userAgent)) {
    bannerDetDate.classList.add('noios-padding');
    bannerDetTime.classList.add('noios-padding');
  }
});
